mapper.insert:
insert into
	productboard
	(
		num
		,productcode
		,id
		,title
		,content
		
		,originalfile
		,savedfile
		
	) values (
		productboard_seq.nextval
		,#{productcode}
		,#{id}
		,#{title}
		,#{content}
		
		,#{originalfile}
		,#{savedfile}
		
	)

mapper.update:
update
	productboard
set
	title=#{title}
	,content=#{content}
	
	,originalfile=#{originalfile}
	,savedfile=#{savedfile}
	
where
	num=#{num}	
and
	id=#{id}

mapper.delete:
delete from
	productboard
where
	num=#{num}	
and
	id=#{id}
